import { useCallback } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import { LinkContainer } from "react-router-bootstrap";

function MyNavBar() {
  const scrollToTop = useCallback(() => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  }, []);

  return (
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" sticky="top">
      <Container>
        <LinkContainer to="/" onClick={scrollToTop}>
          <Navbar.Brand className="sr-only">PIZZA</Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="me-auto">
            <LinkContainer to="#favorite">
              <Nav.Link>Favoritos</Nav.Link>
            </LinkContainer>
            <LinkContainer to="#sweets">
              <Nav.Link>Postres</Nav.Link>
            </LinkContainer>
            <NavDropdown title="Info" id="collasible-nav-dropdown">
              <LinkContainer to="#time">
                <NavDropdown.Item>Horarios</NavDropdown.Item>
              </LinkContainer>
              <LinkContainer to="#about">
                <NavDropdown.Item>About Us</NavDropdown.Item>
              </LinkContainer>
              <LinkContainer to="#location">
                <NavDropdown.Item>Localizaci√≥n</NavDropdown.Item>
              </LinkContainer>
              <NavDropdown.Divider />
              <LinkContainer to="/menu">
                <NavDropdown.Item>Menu</NavDropdown.Item>
              </LinkContainer>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default MyNavBar;
